$(document).mousemove(function(e){windowWidth=$(window).width(),windowHeight=$(window).height(),mouseXpercentage=Math.round(e.pageX/windowWidth*100),mouseYpercentage=Math.round(e.pageY/windowHeight*100),$(".radial-gradient").css("background","radial-gradient(at "+mouseXpercentage+"% "+mouseYpercentage+"%, #3498db, #9b59b6)")});